from django.core.urlresolvers import reverse from django.utils.translation import ugettext_lazy as _
from horizon import exceptions from horizon import forms
from openstack_dashboard import api
class CreateSnapshot(forms.SelfHandlingForm): instance_id = forms.CharField(label=_("Instance ID"), widget=forms.HiddenInput(), required=False) name = forms.CharField(max_length=255, label=_("Snapshot Name"))
def handle(self, request, data): try: snapshot = api.nova.snapshot_create(request, data['instance_id'], data['name']) return snapshot except Exception: exceptions.handle(request, _('Unable to create snapshot.'))

from django.utils.translation import ugettext_lazy as _
from horizon import tables
def is_deleting(instance): task_state = getattr(instance, "OS-EXT-STS:task_state", None) if not task_state: return False return task_state.lower() == "deleting"
class CreateSnapshotAction(tables.LinkAction): name = "snapshot" verbose_name = _("Create Snapshot") url = "horizon:mydashboard:mypanel:create_snapshot" classes = ("ajax-modal",) icon = "camera"
def allowed(self, request, instance=None): return instance.status in ("ACTIVE") \ and not is_deleting(instance)
class MyFilterAction(tables.FilterAction): name = "myfilter"
class InstancesTable(tables.DataTable): name = tables.Column("name", verbose_name=_("Name")) status = tables.Column("status", verbose_name=_("Status")) zone = tables.Column('availability_zone', verbose_name=_("Availability Zone")) image_name = tables.Column('image_name', verbose_name=_("Image Name"))
class Meta(object): name = "instances" verbose_name = _("Instances") table_actions = (MyFilterAction,) row_actions = (CreateSnapshotAction,)
